import { useContext, useState } from "react";
import "./insertComment.styles.scss";
import { CommunityContext } from "../../utilities/context/community.context";
import { UserContext } from "../../utilities/context/user.context";

const InsertComment = ({ postID }) => {
  const [postComment, setPostComment] = useState("");
  const { commentData, setCommentData } = useContext(CommunityContext);
  const { currentUser } = useContext(UserContext);

  const handleInputChange = (event) => {
    setPostComment(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const newPost = {
      commentID: commentData.length + 1,
      postID: postID,
      commentText: postComment,
      dateCreated: new Date().toISOString(),
      createdBy: {
        userID: currentUser.uid,
        username: currentUser.displayName,
        displayProfile: currentUser.photoURL,
      },
    };

    setCommentData([...commentData, newPost]);
    console.log(newPost);
    setPostComment(""); // Clear the input field after submission
  };

  return (
    <div className="insertCommentContainer">
      <form onSubmit={handleSubmit}>
        <input
          placeholder="Enter Comment"
          value={postComment}
          onChange={handleInputChange}
          disabled={!currentUser}
        />
        <div className="CommentButtonContainer">
          <button
            className="rounded-none"
            type="submit"
            disabled={!currentUser}>
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default InsertComment;
