import { useState, useContext } from "react";
import Posts from "../posts/posts.component";
import Sidebar from "../sidebar/sidebar.component";
import "./postPage.styles.scss";
import { CommunityContext } from "../../utilities/context/community.context";

const PostPage = () => {
  const [postDescription, setPostDescription] = useState("");
  const { postData, commentData, setPostData } = useContext(CommunityContext);

  const handleInputChange = (event) => {
    setPostDescription(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Submitted post content:", postDescription);
    // Add logic to handle the submitted post content, e.g., send it to an API or update state
    setPostDescription(""); // Clear the input field after submission
  };

  return (
    <div className="postpageContainer">
      <Sidebar />
      <section>
        <div className="post">
          <form onSubmit={handleSubmit}>
            <input
              placeholder="Enter Post"
              value={postDescription}
              onChange={handleInputChange}
            />
            <div className="postButtonContainer">
              <button className="rounded-none" type="submit">
                Submit
              </button>
              <button type="button" onClick={() => setPostDescription("")}>
                Clear
              </button>
            </div>
          </form>
        </div>
        <Posts />
      </section>
    </div>
  );
};

export default PostPage;
