import React from "react";
import "./comments.styles.scss";

const Comments = ({ commentDatas, postID, enableComment }) => {
  // Filter comments for the specific postID and sort them by dateCreated (newest to oldest)
  const sortedComments = commentDatas
    .filter((comment) => comment.postID === postID)
    .sort((a, b) => new Date(b.dateCreated) - new Date(a.dateCreated));

  return (
    <div className="commentContainer">
      {sortedComments.map((comment, index) => (
        <div key={index}>{comment.commentText}</div>
      ))}
    </div>
  );
};

export default Comments;
