import "./posts.styles.scss";
import postDatas from "../../resources/postData.json";
import commentDatas from "../../resources/commentData.json";
import { useState } from "react";
import Comments from "../comments/comments.components";

const Posts = () => {
  const [enableComment, setEnableComment] = useState(false);

  const postID = (id) => {
    console.log(id);
  };

  return (
    <div className="postsContainer">
      {postDatas.map((data) => {
        return (
          <div
            className="postsContentsContainer"
            onClick={() => postID(data.ID)}
            key={data.ID}>
            <p>{data.postDescription}</p>
            <hr />
            <div>
              <div>{data.likedBy.length} Liked</div>
              <div onClick={() => setEnableComment(!enableComment)}>
                Comment
              </div>
            </div>
            {enableComment && (
              <Comments
                commentDatas={commentDatas}
                postID={postID}
                enableComment={enableComment}
              />
            )}
          </div>
        );
      })}
    </div>
  );
};

export default Posts;
